import React, { useEffect, useState } from "react";
import { fetchData } from "../api";
import { Bar } from "react-chartjs-2"; // Chart.js integration

const CategorizedData = ({ endpoint }) => {
  const [data, setData] = useState([]);
  const [error, setError] = useState(null);
  const [search, setSearch] = useState(""); // Search input
  const [chartData, setChartData] = useState(null); // Data for the chart

  // Fetch data from the backend
    useEffect(() => {
    const getData = async () => {
      try {
        const result = await fetchData(endpoint);
        console.log("Fetched Data:", result); // Log fetched data to check format
        if (Array.isArray(result)) {
          setData(result); // Set data only if it's an array
        } else if (result && typeof result === "object") {
          // Convert object values to an array
          setData(Object.values(result));
        } else {
          setData([]); // Fallback to an empty array
        }
      } catch (err) {
        console.error("Error fetching data:", err.message);
        setError(err.message);
      }
    };
    getData();
  }, [endpoint]);

  // Apply search filter
  const filteredData = data.filter((row) =>
    Object.values(row).some((val) =>
      val.toString().toLowerCase().includes(search.toLowerCase())
    )
  );

  // Prepare data for the chart (e.g., currency distribution)
  useEffect(() => {
    const currencyCount = {};
    data.forEach((item) => {
      currencyCount[item.currency] = (currencyCount[item.currency] || 0) + 1;
    });
    setChartData({
      labels: Object.keys(currencyCount),
      datasets: [
        {
          label: "Currency Distribution",
          data: Object.values(currencyCount),
          backgroundColor: "rgba(54, 162, 235, 0.6)",
        },
      ],
    });
  }, [data]);

  if (error) return <p className="text-red-500 font-semibold">{error}</p>;
  if (!data.length) return <p className="text-gray-500 font-semibold">Loading...</p>;

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">{endpoint.toUpperCase()} Data</h2>

      {/* Search Bar */}
      <input
        type="text"
        placeholder="Search..."
        className="border p-2 mb-4 w-full"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />

      {/* Chart */}
      {chartData && (
        <div className="mb-8">
          <h3 className="text-lg font-semibold mb-2">Currency Distribution</h3>
          <Bar data={chartData} />
        </div>
      )}

      {/* Table */}
      <DataTable data={filteredData} />
    </div>
  );
};

// DataTable Component for displaying table
const DataTable = ({ data }) => {
  if (!Array.isArray(data) || data.length === 0) {
    return <p className="text-center text-gray-500">No data available.</p>;
  }

  // Extract column headers dynamically from the first row
  const tableHeaders = Object.keys(data[0]);

  return (
    <div className="overflow-x-auto">
      <table className="min-w-full table-auto border border-gray-300">
        {/* Table Header */}
        <thead>
          <tr className="bg-blue-600 text-white">
            {tableHeaders.map((header, index) => (
              <th
                key={index}
                className="py-3 px-4 border border-gray-300 text-left capitalize"
              >
                {header.replace(/([A-Z])/g, " $1").trim()} {/* Format camelCase */}
              </th>
            ))}
          </tr>
        </thead>

        {/* Table Body */}
        <tbody>
          {data.map((row, rowIndex) => (
            <tr
              key={rowIndex}
              className={`${
                rowIndex % 2 === 0 ? "bg-gray-50" : "bg-white"
              } hover:bg-gray-200`}
            >
              {tableHeaders.map((header, colIndex) => (
                <td
                  key={colIndex}
                  className="py-3 px-4 border border-gray-300 text-gray-700"
                >
                  {/* Handle null, undefined, or nested objects */}
                  {row[header] !== null &&
                  row[header] !== undefined &&
                  typeof row[header] !== "object"
                    ? row[header]
                    : row[header] && typeof row[header] === "object"
                    ? JSON.stringify(row[header])
                    : "N/A"}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default CategorizedData;
