import React, { useEffect, useState } from "react";
import { fetchData } from "../api";
import Table from "react-bootstrap/Table";
import Form from "react-bootstrap/Form";

const CategorizedData = ({ endpoint }) => {
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [search, setSearch] = useState(""); // Search input
  const [error, setError] = useState(null);

  // Fetch data from the backend
  useEffect(() => {
    const getData = async () => {
      try {
        const result = await fetchData(endpoint);
        const transformedData = result.hits.hits.map((item) => item._source);
        setData(transformedData);
        setFilteredData(transformedData);
      } catch (err) {
        setError(err.message);
      }
    };
    getData();
  }, [endpoint]);

  // Handle search
  const handleSearch = (e) => {
    const searchTerm = e.target.value.toLowerCase();
    setSearch(searchTerm);

    const filtered = data.filter((row) =>
      Object.values(row).some((val) =>
        val.toString().toLowerCase().includes(searchTerm)
      )
    );
    setFilteredData(filtered);
  };

  if (error) {
    return (
      <div className="alert alert-danger" role="alert">
        Error: {error}
      </div>
    );
  }

  if (!data.length) {
    return (
      <div className="alert alert-info" role="alert">
        Loading...
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <h2 className="text-center mb-4">{endpoint.toUpperCase()} Data</h2>

      {/* Search Bar */}
      <Form.Control
        type="text"
        placeholder="Search..."
        value={search}
        onChange={handleSearch}
        className="mb-4"
      />

      {/* Table */}
      <Table striped bordered hover responsive>
        <thead className="table-dark">
          <tr>
            {Object.keys(data[0]).map((header, index) => (
              <th key={index}>{header.replace(/([A-Z])/g, " $1").trim()}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {filteredData.map((row, rowIndex) => (
            <tr key={rowIndex}>
              {Object.keys(row).map((key, colIndex) => (
                <td key={colIndex}>
                  {row[key] !== null && row[key] !== undefined
                    ? row[key].toString()
                    : "N/A"}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default CategorizedData;
