import React, { useEffect, useState } from "react";
import { fetchData } from "../api";
import able from "react-bootstrap/Table";
import Alert from "react-bootstrap/Alert";


const CategorizedData = ({ endpoint }) => {
  const [data, setData] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getData = async () => {
      try {
        const result = await fetchData(endpoint);
        console.log("Fetched Data:", result); // Log fetched data to check format
        if (Array.isArray(result)) {
          setData(result); // Set data only if it's an array
        } else if (result && typeof result === "object") {
          // Convert object values to an array
          setData(Object.values(result));
        } else {
          setData([]); // Fallback to an empty array
        }
      } catch (err) {
        console.error("Error fetching data:", err.message);
        setError(err.message);
      }
    };
    getData();
  }, [endpoint]);

  if (error)
    return <p className="text-center text-red-500 font-semibold">Error: {error}</p>;
  if (!data || data.length === 0)
    return <p className="text-center text-gray-500 font-semibold">loading....</p>;

  return (
    <div className="container mt-4">
      <h2 className="text-center mb-4">{endpoint.toUpperCase()} Data</h2>
      <DataTable data={data} />
    </div>
  );
};

const DataTable = ({ data }) => {
  if (!Array.isArray(data) || data.length === 0) {
    return (
      <Alert variant="warning" className="text-center">
        No data available.
      </Alert>
    );
  }

  // Extract headers dynamically from the first row
  const tableHeaders = Object.keys(data[0]);

  return (
    <div className="overflow-auto">
      <table striped bordered hover responsive className="mt-3">
        {/* Table Header */}
        <thead className="table-dark">
          <tr>
            {tableHeaders.map((header, index) => (
              <th key={index} className="text-capitalize">
                {header.replace(/([A-Z])/g, " $1").trim()} {/* Format camelCase */}
              </th>
            ))}
          </tr>
        </thead>

        {/* Table Body */}
        <tbody>
          {data.map((row, rowIndex) => (
            <tr key={rowIndex}>
              {tableHeaders.map((header, colIndex) => (
                <td key={colIndex}>
                  {row[header] !== null &&
                  row[header] !== undefined &&
                  typeof row[header] !== "object"
                    ? row[header]
                    : row[header] && typeof row[header] === "object"
                    ? JSON.stringify(row[header])
                    : "N/A"}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default CategorizedData;
