import React, { useEffect, useState } from "react";
import { Table } from "react-bootstrap";

const CategorizedData = ({ endpoint }) => {
  const [data, setData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/${endpoint}`);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const result = await response.json();

        if (result && typeof result === "object" && Object.keys(result).length > 0) {
          setData(result); // Set data only if it has content
        } else {
          setData(null); // Fallback for empty or invalid data
        }
      } catch (err) {
        setError(err.message);
      }
    };
    fetchData();
  }, [endpoint]);

  if (error) {
    return <p className="text-danger text-center">Error: {error}</p>;
  }

  if (!data) {
    return <p className="text-center">No data available.</p>;
  }

  return (
    <div className="p-4">
      <h2 className="text-center">{endpoint.toUpperCase()} Data</h2>
      <TabsWithTable data={data} />
    </div>
  );
};

// Tabs and Table Component
const TabsWithTable = ({ data }) => {
  const [activeTab, setActiveTab] = useState(Object.keys(data)[0]);

  if (!data || Object.keys(data).length === 0) {
    return <p>No grouped data to display.</p>;
  }

  return (
    <div>
      <ul className="nav nav-tabs">
        {Object.keys(data).map((key) => (
          <li className="nav-item" key={key}>
            <button
              className={`nav-link ${activeTab === key ? "active" : ""}`}
              onClick={() => setActiveTab(key)}
            >
              {key.toUpperCase()}
            </button>
          </li>
        ))}
      </ul>

      <div className="mt-3">
        {data[activeTab] && <DataTable rows={data[activeTab]} />}
      </div>
    </div>
  );
};

// Table Component
const DataTable = ({ rows }) => {
  if (!rows || rows.length === 0) {
    return <p>No records found for the selected tab.</p>;
  }

  const headers = Object.keys(rows[0]);

  return (
    <Table striped bordered hover responsive>
      <thead>
        <tr>
          {headers.map((header) => (
            <th key={header} className="text-capitalize">
              {header.replace(/([A-Z])/g, " $1").trim()}
            </th>
          ))}
        </tr>
      </thead>
      <tbody>
        {rows.map((row, index) => (
          <tr key={index}>
            {headers.map((header) => (
              <td key={header}>{row[header] ?? "N/A"}</td>
            ))}
          </tr>
        ))}
      </tbody>
    </Table>
  );
};

export default CategorizedData;
