import React, { useState, useEffect } from 'react';
import { fetchData } from '../api'; // Adjust the import to match your path

const ExchangeData = () => {
  const [columns, setColumns] = useState([]);
  const [records, setRecords] = useState([]);

  const fetchExchangeData = async () => {
    try {
      const data = await fetchData('exchange');
      if (data && data.hits && data.hits.hits) {
        const exchangeData = data.hits.hits.map(hit => hit._source);
        setColumns(Object.keys(exchangeData[0])); // Assuming all records have the same structure
        setRecords(exchangeData);
      } else {
        console.error('Data structure is not as expected.');
      }
    } catch (error) {
      console.error('Error fetching exchange data:', error.message);
    }
  };

  useEffect(() => {
    fetchExchangeData();
  }, []);

  return (
    <div>
      <h1>Exchange Data</h1>
      {columns.length > 0 && (
        <table>
          <thead>
            <tr>
              {columns.map((column, index) => (
                <th key={index}>{column}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {records.map((record, index) => (
              <tr key={index}>
                {columns.map((column, index) => (
                  <td key={index}>{record[column]}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ExchangeData;
