import React, { useEffect, useState } from "react";
import { fetchData } from "../api";

const CategorizedData = ({ endpoint }) => {
  const [data, setData] = useState([]); // All fetched data
  const [filteredData, setFilteredData] = useState([]); // Filtered data for display
  const [error, setError] = useState(null);
  const [search, setSearch] = useState(""); // Search term
  const [currentPage, setCurrentPage] = useState(1); // Current page
  const rowsPerPage = 5; // Number of rows per page

  useEffect(() => {
    const getData = async () => {
      try {
        const result = await fetchData(endpoint);
        if (Array.isArray(result)) {
          setData(result);
          setFilteredData(result); // Initialize filtered data
        } else if (result && typeof result === "object") {
          const values = Object.values(result);
          setData(values);
          setFilteredData(values);
        } else {
          setData([]);
          setFilteredData([]);
        }
      } catch (err) {
        setError(err.message);
      }
    };
    getData();
  }, [endpoint]);

  // Handle search filter
  useEffect(() => {
    if (search.trim() === "") {
      setFilteredData(data);
    } else {
      const lowerSearch = search.toLowerCase();
      setFilteredData(
        data.filter((item) =>
          Object.values(item).some(
            (value) =>
              value &&
              value.toString().toLowerCase().includes(lowerSearch)
          )
        )
      );
    }
  }, [search, data]);

  // Pagination logic
  const startIndex = (currentPage - 1) * rowsPerPage;
  const paginatedData = filteredData.slice(
    startIndex,
    startIndex + rowsPerPage
  );

  if (error) {
    return <p className="text-center text-red-500 font-semibold">Error: {error}</p>;
  }

  if (!filteredData || filteredData.length === 0) {
    return <p className="text-center text-gray-500 font-semibold">No data available.</p>;
  }

  return (
    <div className="p-4">
      <h2 className="text-2xl font-semibold text-center text-blue-600 mb-4">
        {endpoint.charAt(0).toUpperCase() + endpoint.slice(1)} Data
      </h2>

      {/* Search Bar */}
      <div className="flex justify-center mb-4">
        <input
          type="text"
          className="border border-gray-300 rounded-lg px-4 py-2 w-1/2 focus:outline-none focus:ring focus:ring-blue-300"
          placeholder="Search..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
      </div>

      {/* Data Table */}
      <DataTable data={paginatedData} />

      {/* Pagination Controls */}
      <Pagination
        currentPage={currentPage}
        totalRows={filteredData.length}
        rowsPerPage={rowsPerPage}
        onPageChange={(page) => setCurrentPage(page)}
      />
    </div>
  );
};

const DataTable = ({ data }) => {
  if (!Array.isArray(data) || data.length === 0) {
    return <p className="text-center text-gray-500">No records to display.</p>;
  }

  const headers = Object.keys(data[0]);

  return (
    <div className="overflow-x-auto mt-4">
      <table className="min-w-full table-auto border border-gray-300">
        <thead>
          <tr className="bg-blue-600 text-white">
            {headers.map((header, index) => (
              <th
                key={index}
                className="py-2 px-4 text-left border border-gray-300 capitalize"
              >
                {header.replace(/([A-Z])/g, " $1").trim()}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {data.map((row, rowIndex) => (
            <tr
              key={rowIndex}
              className={`${
                rowIndex % 2 === 0 ? "bg-gray-50" : "bg-white"
              } hover:bg-gray-200`}
            >
              {headers.map((header, colIndex) => (
                <td key={colIndex} className="py-2 px-4 border border-gray-300">
                  {row[header] !== null &&
                  row[header] !== undefined &&
                  typeof row[header] !== "object"
                    ? row[header]
                    : JSON.stringify(row[header])}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const Pagination = ({ currentPage, totalRows, rowsPerPage, onPageChange }) => {
  const totalPages = Math.ceil(totalRows / rowsPerPage);

  if (totalPages <= 1) {
    return null; // Hide pagination for single-page data
  }

  return (
    <div className="flex justify-center mt-4">
      <button
        className="px-3 py-1 border border-gray-300 rounded-l-lg bg-gray-100 hover:bg-gray-200"
        onClick={() => onPageChange(currentPage - 1)}
        disabled={currentPage === 1}
      >
        Previous
      </button>
      {[...Array(totalPages).keys()].map((page) => (
        <button
          key={page}
          className={`px-3 py-1 border border-gray-300 ${
            currentPage === page + 1 ? "bg-blue-600 text-white" : "bg-gray-100"
          } hover:bg-gray-200`}
          onClick={() => onPageChange(page + 1)}
        >
          {page + 1}
        </button>
      ))}
      <button
        className="px-3 py-1 border border-gray-300 rounded-r-lg bg-gray-100 hover:bg-gray-200"
        onClick={() => onPageChange(currentPage + 1)}
        disabled={currentPage === totalPages}
      >
        Next
      </button>
    </div>
  );
};

export default CategorizedData;
