import React, { useState } from "react";
import { Tab, Tabs } from "react-bootstrap";
import Table from "react-bootstrap/Table";
import Form from "react-bootstrap/Form";

const CategorizedData = ({ data }) => {
  const [search, setSearch] = useState(""); // Search query
  const [activeKey, setActiveKey] = useState(Object.keys(data)[0]); // Active tab key

  // Filter data based on the search query
  const filterData = (items) => {
    return items.filter((item) =>
      Object.values(item).some((value) =>
        value?.toString().toLowerCase().includes(search.toLowerCase())
      )
    );
  };

  return (
    <div className="container mt-4">
      <h2 className="text-center mb-4">Exchange Data</h2>

      {/* Search Bar */}
      <Form.Control
        type="text"
        placeholder="Search..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        className="mb-4"
      />

      {/* Tabs for Grouped Data */}
      <Tabs
        activeKey={activeKey}
        onSelect={(k) => setActiveKey(k)}
        className="mb-3"
      >
        {Object.keys(data).map((type) => (
          <Tab key={type} eventKey={type} title={type.toUpperCase()}>
            <ResponsiveTable data={filterData(data[type])} />
          </Tab>
        ))}
      </Tabs>
    </div>
  );
};

// Responsive Table Component
const ResponsiveTable = ({ data }) => {
  if (data.length === 0) {
    return <p className="text-center text-gray-500">No matching records found.</p>;
  }

  const headers = Object.keys(data[0]);

  return (
    <Table striped bordered hover responsive>
      <thead className="table-dark">
        <tr>
          {headers.map((header, index) => (
            <th key={index} className="text-capitalize">
              {header.replace(/([A-Z])/g, " $1").trim()}
            </th>
          ))}
        </tr>
      </thead>
      <tbody>
        {data.map((row, rowIndex) => (
          <tr key={rowIndex}>
            {headers.map((header, colIndex) => (
              <td key={colIndex}>
                {row[header] !== null &&
                row[header] !== undefined &&
                typeof row[header] !== "object"
                  ? row[header]
                  : JSON.stringify(row[header]) || "N/A"}
              </td>
            ))}
          </tr>
        ))}
      </tbody>
    </Table>
  );
};

export default CategorizedData;
