import React, { useEffect, useState } from "react";
import { fetchData } from "../api";
import DataTable from "./DataTable";
import { Bar } from "react-chartjs-2";

const CategorizedData = ({ endpoint }) => {
  const [data, setData] = useState([]);
  const [error, setError] = useState(null);
  const [search, setSearch] = useState(""); // Search filter
  const [chartData, setChartData] = useState(null); // Chart data

  useEffect(() => {
    const getData = async () => {
      try {
        const result = await fetchData(endpoint);
        setData(result.hits.hits.map((item) => item._source)); // Extract '_source' data
      } catch (err) {
        setError(err.message);
      }
    };
    getData();
  }, [endpoint]);

  // Filtered data based on search
  const filteredData = data.filter((row) =>
    Object.values(row).some((val) => val.toString().toLowerCase().includes(search.toLowerCase()))
  );

  // Generate chart data (e.g., for 'currency' field count)
  useEffect(() => {
    const currencyCount = {};
    data.forEach((item) => {
      currencyCount[item.currency] = (currencyCount[item.currency] || 0) + 1;
    });
    setChartData({
      labels: Object.keys(currencyCount),
      datasets: [
        {
          label: "Currency Distribution",
          data: Object.values(currencyCount),
          backgroundColor: "rgba(54, 162, 235, 0.6)",
        },
      ],
    });
  }, [data]);

  if (error) return <p className="text-red-500">{error}</p>;
  if (!data.length) return <p>Loading...</p>;

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">{endpoint.toUpperCase()} Data</h2>

      {/* Search Filter */}
      <input
        type="text"
        placeholder="Search..."
        className="border p-2 mb-4 w-full"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />

      {/* Chart */}
      {chartData && (
        <div className="mb-8">
          <h3 className="text-lg font-semibold mb-2">Currency Distribution</h3>
          <Bar data={chartData} />
        </div>
      )}

      {/* Table */}
      <DataTable data={filteredData} />
    </div>
  );
};

export default CategorizedData;
